sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    var payload = JSON.parse(inputs.payload);

    var virtualMachines = {
      'includes': [],
      'excludes': {
        'vms': [],
        'disks': [],
        'templates': {
          'isEnabled': true,
          'excludeFromIncremental': true
        }
      }
    };

    var body = {
      "name": inputs.name,
      "description": inputs.description,
      "type": "Backup",
      "isHighPriority": "false",
      "virtualMachines": virtualMachines,
      "storage": payload.storage,
      "guestProcessing": payload.guestProcessing,
      "schedule": payload.schedule
    };

    var vmGr = new GlideRecord('cmdb_ci_vmware_instance');
    vmGr.addEncodedQuery('sys_idIN' + inputs.virtualMachines.toString());
    vmGr.query();
    while(vmGr.next()) {
      var vmObject = {
        'inventoryObject': {
          'type': 'VirtualMachine',
          'hostName': vmGr.vcenter_ref.ip_address.toString(),
          'name': vmGr.name.toString(),
          'objectId': vmGr.object_id.toString()
        },
        'size': vmGr.disks_size.toString() + ' GB'
      };

      body.virtualMachines['includes'].push(vmObject);

      var diskObject = {
        'disksToProcess': 'AllDisks',
        'vmObject': vmObject,
        'disks': [],
        'removeFromVMConfiguration': true
      };

      body.virtualMachines['excludes'].disks.push(diskObject);
    }


    for(var i=0; i < body.virtualMachines['includes'].length; i++) {
      body.virtualMachines['includes'][i]['name'] = body.virtualMachines['includes'][i].inventoryObject.name;
      body.virtualMachines['includes'][i]['hostName'] = body.virtualMachines['includes'][i].inventoryObject.hostName;
      body.virtualMachines['includes'][i]['type'] = body.virtualMachines['includes'][i].inventoryObject.type;
      body.virtualMachines['includes'][i]['objectId'] = body.virtualMachines['includes'][i].inventoryObject.objectId;
      delete body.virtualMachines['includes'][i].inventoryObject;
    }

	outputs.request_body = JSON.stringify(body);
    outputs.payload = body;

    /*var VeeamAppUtil = new x_tesgh_veeam_app.VeeamAppUtil();
    var baseurl = VeeamAppUtil.getRESTMessageEndpoint(VeeamAppUtil._defaultRESTMessageSysId);

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Create Job');
    r.setStringParameterNoEscape('baseUrl', baseurl);
    r.setStringParameterNoEscape('token', token);
    r.setStringParameterNoEscape('body', JSON.stringify(body));

    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();

    outputs.response_body = responseBody;
    outputs.http_status = httpStatus;*/


    var veeamAppUtil = new x_tesgh_veeam_app.VeeamAppUtil();
    var credentials = JSON.parse(veeamAppUtil.getVeeamCredentials(inputs.veeamConnectionSysID));

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Create Job');
    r.setStringParameterNoEscape('baseUrl', credentials.baseurl);
    r.setStringParameterNoEscape('token', inputs.token);
    r.setStringParameterNoEscape('body', JSON.stringify(body));
 	if (credentials.midserver) r.setMIDServer(credentials.midserver);

    var response = r.execute();

    outputs.response_body = response.getBody();
    outputs.http_status = response.getStatusCode();
  }
  catch(ex) {
    outputs.error_message = ex.message;
  }

})(inputs, outputs);

