sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
    var cmdbVMSysIDs = '';    
    var vmObjects = [];    
    var body = JSON.parse(inputs.oldBody);

    var grVeeamVM = new GlideRecord('x_tesgh_veeam_app_virtual_machine');
    grVeeamVM.addEncodedQuery('sys_idIN' + inputs.vmList);
    grVeeamVM.query();
    while (grVeeamVM.next()) {
    	cmdbVMSysIDs += grVeeamVM.getValue('virtual_machine') + ','; 
    }

    var grVmwareInstance = new GlideRecord('cmdb_ci_vmware_instance');
    grVmwareInstance.addEncodedQuery('sys_idIN' + cmdbVMSysIDs);
    grVmwareInstance.query();
    while (grVmwareInstance.next()) {
      vmObjects.push({
        'inventoryObject': {
          'type': 'VirtualMachine',
          'hostName': grVmwareInstance.vcenter_ref.fqdn.toString(),
          'name': grVmwareInstance.name.toString(),
          'objectId': grVmwareInstance.object_id.toString()
        }
      });
    }

    vmObjects.forEach(function(vmObject) {
      body.virtualMachines.includes.push(vmObject);
    });

	var credentials = JSON.parse(new x_tesgh_veeam_app.VeeamAppUtil().getVeeamCredentials(inputs.veeamConnectionSysID));

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Edit Job');
    r.setStringParameterNoEscape('baseUrl', credentials.baseurl);
    r.setStringParameterNoEscape('token', inputs.token);
    r.setStringParameterNoEscape('id', inputs.jobId);
    r.setStringParameterNoEscape('body', JSON.stringify(body));
	if (credentials.midserver) r.setMIDServer(credentials.midserver);

    var response = r.execute();

    outputs.response_body = response.getBody();
    outputs.http_status = response.getStatusCode();    
  }
  catch(ex) {
    outputs.error_message = ex.message;
  }

})(inputs, outputs);

