sys_variable_value.value:
(function execute(inputs, outputs) {
  try { 

    var token = inputs.token;
    var jobId = inputs.jobId;

    var VeeamAppUtil = new x_tesgh_veeam_app.VeeamAppUtil();
    var baseurl = VeeamAppUtil.getRESTMessageEndpoint(VeeamAppUtil._defaultRESTMessageSysId);

    var requestBody = {
      "performActiveFull": false,
      "startChainedJobs": false
    };

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Start Job');
    r.setStringParameterNoEscape('baseUrl', baseurl);
    r.setStringParameterNoEscape('token', token);
    r.setStringParameterNoEscape('id', jobId);
    r.setStringParameterNoEscape('body', JSON.stringify(requestBody));

    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();

    outputs.response_body = responseBody;
    outputs.http_status = httpStatus;
  }
  catch(ex) {
    outputs.error_message = ex.message;
  }

})(inputs, outputs);

