sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseObject = JSON.parse(inputs.responseBody);
  if(responseObject && responseObject.id) {
    var sessionObject = {
      "id": responseObject.id,
      "state": responseObject.state,
      "name": responseObject.name,
      "sessionType": responseObject.sessionType,
      "creationTime": responseObject.creationTime
    };
    outputs.session = sessionObject;

	responseObject.ritmContext = inputs.ritmSysId

    var jsonBody = { "records": [] };
    jsonBody.records.push(responseObject);

    var base_url = gs.getProperty('glide.servlet.uri');

    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(base_url + 'api/now/import/x_tesgh_veeam_app_import_veeam_sessions/insertMultiple');
    request.setHttpMethod('POST');
    request.setAuthenticationProfile('basic', '39480e731b299910e06d520b234bcb48');
    request.setRequestHeader("Accept","application/json");
    request.setRequestHeader('Content-Type','application/json');
    request.setRequestBody(JSON.stringify(jsonBody));
    var response = request.execute();
    outputs.body = response.getBody();

  }

})(inputs, outputs);

