sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.responseBody) {
    var jsonResponse = JSON.parse(inputs.responseBody);    
    var vmIncludesArr = jsonResponse.virtualMachines['includes'];

    var objIdArr = [];
    var deletedVmArr = [];
    var addedVmArr = [];

    vmIncludesArr.forEach(function(vm) {
      objIdArr.push(vm.inventoryObject.objectId);
    });

    var grVeeamVM = new GlideRecord('x_tesgh_veeam_app_virtual_machine');
    grVeeamVM.addEncodedQuery('backup_job.id=' + jsonResponse.id + '^virtual_machine.object_idNOT IN' + objIdArr.join(','));
    grVeeamVM.query();
    while (grVeeamVM.next()) {
      deletedVmArr.push({
        'name': grVeeamVM.virtual_machine.getDisplayValue(),
        'sys_id': grVeeamVM.getUniqueValue()
      });        
      grVeeamVM.backup_job = '';
      grVeeamVM.backup_job_template = '';
      grVeeamVM.update();
    }

    vmIncludesArr.forEach(function(vm) {
      var grVmInstance = new GlideRecord('cmdb_ci_vmware_instance');
      grVmInstance.addEncodedQuery('object_id=' + vm.inventoryObject.objectId + '^vcenter_ref.fqdn=' + vm.inventoryObject.hostName);
		  grVmInstance.setLimit(1);
      grVmInstance.query();
      if (grVmInstance.next()) {
        var grVeeamVM = new GlideRecord('x_tesgh_veeam_app_virtual_machine');
        if (grVeeamVM.get('virtual_machine', grVmInstance.getUniqueValue())) {
          if (grVeeamVM.backup_job != inputs.jobSysId) {
            addedVmArr.push({
              'name': grVeeamVM.virtual_machine.getDisplayValue(),
              'sys_id': grVeeamVM.getUniqueValue()
            }); 
            grVeeamVM.backup_job = inputs.jobSysId;
            grVeeamVM.update();
          }          
        }
      }
    });

    outputs.deleted_vms = JSON.stringify(deletedVmArr);   
    outputs.added_vms = JSON.stringify(addedVmArr);
  }
  
})(inputs, outputs);

