sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBodyObj = JSON.parse(inputs.responseBody);

  if (responseBodyObj && responseBodyObj.data) {
    var jobsArr = [];

    responseBodyObj.data.forEach(function(job) {
      if (job.platformName.toLowerCase() == "vmware") {
        job.veeamConnection = inputs.veeamConnectionSysID;
        jobsArr.push(job);
      }
    });

    var importJSON = {"records": jobsArr};

    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(gs.getProperty('glide.servlet.uri') + 'api/now/import/x_tesgh_veeam_app_import_job_mappings/insertMultiple');
    request.setHttpMethod('POST');
    request.setAuthenticationProfile('basic', '39480e731b299910e06d520b234bcb48');
    request.setRequestHeader('Accept', 'application/json');
    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestBody(JSON.stringify(importJSON));

    var response = request.execute();

    outputs.body = response.getBody();
  }

})(inputs, outputs);

