sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBodyObject = JSON.parse(inputs.responseBody);

  if (responseBodyObject && responseBodyObject.data.length) {
    var jobIdArray = [];
    var deletedJobMappingCount = 0;
    var deletedJobCount = 0;

    responseBodyObject.data.forEach(function(job) {
        jobIdArray.push(job.jobId);
    });

    var jobIdString= jobIdArray.join(',');

    var grJobMapping = new GlideRecord('x_tesgh_veeam_app_job_mapping');
    grJobMapping.addEncodedQuery('job_idNOT IN' + jobIdString);
    grJobMapping.query();
    if (grJobMapping.hasNext()) {
      deletedJobMappingCount = grJobMapping.getRowCount();
      grJobMapping.deleteMultiple();
    }

    var grJob = new GlideRecord('x_tesgh_veeam_app_backup_job');
    grJob.addEncodedQuery('idNOT IN' + jobIdString);
    grJob.query();
    while (grJob.next()) {
      var grVirtualMachine = new GlideRecord('x_tesgh_veeam_app_virtual_machine');
      grVirtualMachine.addQuery('backup_job', grJob.getUniqueValue());
      grVirtualMachine.query();
      while (grVirtualMachine.next()) {
        grVirtualMachine.backup_job_template = '';
      	grVirtualMachine.backup_job = '';
        grVirtualMachine.update();
      }

      deletedJobCount++;

      grJob.deleteRecord();
    }

    outputs.deleted_job_mapping_count = deletedJobMappingCount; 
  	outputs.deleted_job_count = deletedJobCount; 
  }

})(inputs, outputs);

